export default Mailer= (obj)=>{


    dotenv.config();

    const templateId = process.env.USER;
    const templateData = {
                from_name: obj.state.data.name,
                reply_to: obj.state.data.email,
                message_html: obj.state.data.message
    }


    window.emailjs.send( 'gmail', templateId, templateData )
            .then(res=>console.log(res))
            .catch(err=>console.log(err))

};
