import '../Styles/Form.css'
import '../Styles/Ant.css'
import ant from '../images/icons8-lizard-50.png'

 class ContForm extends Component {
    state= {
      data : {
              name: "",
              email: "",
              message: ""
      }
    }


handleChange = (e)=>{

const dataCopy = {...this.state.data};
dataCopy[e.target.id] = e.target.value;
  this.setState({
        data: dataCopy
  })

}    


  render() {
    return (
      <div className="form">
          <h2 className="cont_h">
            FOR PROJECTS OR ENQUIRES,
            CONTACT ME
          </h2>
          <form action="" className="formb">
              <div className="li">
                <label htmlFor="name">
                    NAME:
                </label>
                <input type="text" onChange={this.handleChange}  placeholder="PLEASE ENTER YOUR NAME"  id="name"/>
              </div>
              <div className="li">
                <label htmlFor="email">
                    EMAIL:
                </label>
                <input type="text" onChange={this.handleChange} placeholder="PLEASE ENTER YOUR NAME"  id="email"/>
              </div>
              <div className="lia">
                <label htmlFor="message">
                    MESSAGE:
                </label>
                <textarea id="message" cols="30" onChange={this.handleChange} rows="10"></textarea>
              </div>
              <button>SUBMIT</button>
          </form>
          <div className="ant" >
              <div className="tic">UP</div>
          </div>

      </div>
    )
  }
}

export default ContForm;
